    <script>
    

        jQuery(function($) {
            // Asynchronously Load the map API 
            //var script = document.createElement('script');
            //script.src = "//maps.googleapis.com/maps/api/js?sensor=false&callback=initialize";
            //document.body.appendChild(script);
        });
        $(document).ready(function() {
    
            getAll();
        });
    
       
      //  function initialize() {
            var map = new google.maps.Map(document.getElementById('map'), {
                zoom: 10,
    
                scrollwheel: false,
                panControl: true,
                zoomControl: true,
                zoomControlOptions: {
                    style: google.maps.ZoomControlStyle.LARGE
                },
                scaleControl: true,
    
                mapTypeId: google.maps.MapTypeId.ROADMAP
            });
     
            var bounds = new google.maps.LatLngBounds();
            var marker, i;
           
            var markers = [];
            var geocoder = new google.maps.Geocoder();
    
            // Display multiple markers on a map
            var infoWindow = new google.maps.InfoWindow(), marker, i;
    
            function getAll() {
    
    
                // Loop through our array of markers & place each one on the map  
                for (i = 0; i < provinces.length; i++) {
                    var data = provinces[i][1];
                    var res = data.split(",");
                    var position = new google.maps.LatLng(res[0],res[1]);
               
                    bounds.extend(position);
                    marker = new google.maps.Marker({
                        position: position,
                        map: map,
                        icon: base_url+'/Content/images/kia_icon_mapmaker.jpg',
                        title: provinces[i][0]
                    });
    
                    // Allow each marker to have an info window    
                    google.maps.event.addListener(marker, 'click', (function(marker, i) {
                        return function() {
                            infoWindow.setContent(getTitles(provinces[i][0]));
                            infoWindow.open(map, marker);
                        }
                    })(marker, i));
                    markers.push(marker);
                    // Automatically center the map fitting all markers on the screen
                    map.fitBounds(bounds);
                }
    
                // Override our map zoom level once our fitBounds function runs (Make sure it only runs once)
                var boundsListener = google.maps.event.addListener((map), 'bounds_changed', function(event) {
                    //this.setZoom(14);
                    google.maps.event.removeListener(boundsListener);
                });
    
    
            }
         
    
    
    
    
    
     //   }
    
    
        function codeAddress(local) {
            geocoder.geocode({
                'address': local[0]
            }, function (results, status) {
                if (status == google.maps.GeocoderStatus.OK) {
                  
                    var res = local[3].split(",");
                 
                    var myLatlng = new google.maps.LatLng(res[0].trim(),res[1].trim());
                    map.setCenter(results[0].geometry.location);
                    var marker = new google.maps.Marker({
                        map: map,
                        title: local[0],
                            
                        position: myLatlng,
    
                        url: local[2],
                        // position: results[0].geometry.location,
                        icon: base_url+'/Content/images/kia_icon_mapmaker.jpg'
                    });
                    markers.push(marker);
                    bounds.extend(marker.getPosition());
                    map.fitBounds(bounds);
    
                    google.maps.event.addListener(marker, 'dblclick', function () {
                        window.location.href = this.url;
                    });
    
                    google.maps.event.addListener(marker, 'click', (function (marker, location) {
                        return function () {
                            infoWindow.setContent(getTitle(local[1], local[0]));
                            infoWindow.open(map, marker);
                        };
                    })(marker, local));
    
    
    
                } else if (status === google.maps.GeocoderStatus.OVER_QUERY_LIMIT) {
                    setTimeout(function () {
                        codeAddress(local);
                    }, 20);
                } else if (status === google.maps.GeocoderStatus.ZERO_RESULTS) {
    
    
                }
    
                else {
                    alert("Geocode was not successful for the following reason:"
                        + status);
                }
            });
        }
        function codeProvince(location) {
               
            var plot = new google.maps.LatLng(location[1]);
       
            //map.setCenter(plot);
            bounds.extend(plot);
            var marker = new google.maps.Marker({
                map: map,
                title: location[0],
                url: location[0],
                position: plot,
                icon: base_url+'/Content/images/kia_icon_mapmaker.jpg'
            });
            markers.push(marker);
               
            map.fitBounds(bounds);
    
    
    
    
        }
        function showAddress(id) {
            resetMap();
             var map = new google.maps.Map(document.getElementById('map'), {
                zoom: 15,
    
                scrollwheel: false,
                panControl: true,
                zoomControl: true,
                zoomControlOptions: {
                    style: google.maps.ZoomControlStyle.LARGE
                },
                scaleControl: true,
    
                mapTypeId: google.maps.MapTypeId.ROADMAP
            });
                  
            var input = $('#toado-' + id).html();
           var address = $("#agent-"+id).html();
            var latlngStr = input.split(',', 2);
              var position = new google.maps.LatLng(latlngStr[0],latlngStr[1]);
               
          
            geocoder.geocode({
                'location': position
            }, function (results, status) {
              
              
                if (status == google.maps.GeocoderStatus.OK) {
                  
                    map.setCenter(results[0].geometry.location);
                    var marker = new google.maps.Marker({
                        map: map,
                       
                        //title: address,
                        //url: address,
                        position: results[0].geometry.location,
                        icon: base_url+'/Content/images/kia_icon_mapmaker.jpg'
                    });
                    
                    markers.push(marker);
                    bounds.extend(marker.getPosition());
                    map.fitBounds(bounds);
               
                    google.maps.event.addListener(marker, 'dblclick', function () {
                        window.location.href = this.url;
                    });
    
                    google.maps.event.addListener(marker, 'click', (function (marker, location) {
                        return function () {
                            infoWindow.setContent(getTitle("", address));
                            infoWindow.open(map, marker);
                        };
                    })(marker, address));
                    map.setCenter(results[0].geometry.location);
                    map.setZoom(18);
                    
    
                } else if (status === google.maps.GeocoderStatus.OVER_QUERY_LIMIT) {
    
                    setTimeout(function () {
    
                    }, 20);
                } else if (status === google.maps.GeocoderStatus.ZERO_RESULTS) {
    
    
                }
    
                else {
                    alert("Geocode was not successful for the following reason:"
                        + status);
                }
            });
        }
        function resetMap() {
            for (var i = 0; i < markers.length; i++) {
    
    
                markers[i].setMap(null);
            }
    
            markers = [];
    
        }
        $("#agency-region").on('change', function () {
    
            setTimeout(
                      function () {
                          loadListAgency();
                      }, 100);
    
            resetMap();
            if ($("#agency-region").val() == "-1") {
    
                getAll();
            } else {
                var valcity = $("#agency-region option:selected").text();
                var locationbycity = jQuery.grep(locations, function (a) {
                    return $.trim(a[2]) == valcity;
                });
    
          
                for (i = 0; i < locationbycity.length; i++) {
    
    
                    codeAddress(locationbycity[i]);
    
                }
    
                geocoder.geocode({ 'address': valcity }, function (results, status) {
                    if (status == google.maps.GeocoderStatus.OK) {
                            
                        map.setCenter(results[0].geometry.location);
                        map.setZoom(11);
    
                    }
                });
    
            }
    
    
        });
    </script>

